#define MK_KINETIC_SPEED                    // Enable Kinetic mode:  Uses a quadratic curve on cursor speed to allow precise movements at the beginning and increases speed thereafter.
#define MOUSEKEY_INTERVAL 8                 // Time between cursor movements in milliseconds
#define MOUSEKEY_MOVE_DELTA 72              // Step size for accelerating from initial to base speed (default: 16)
#define MOUSEKEY_BASE_SPEED 5000            // Maximum cursor speed at which acceleration stops
#define MOUSEKEY_DECELERATED_SPEED 2500     // Decelerated cursor speed
#define MOUSEKEY_ACCELERATED_SPEED 5000     // Accelerated cursor speed
#define MOUSEKEY_INITIAL_SPEED 300          // Initial speed of the cursor in pixels per second (default: 100)